      headers: {
        'Content-type': 'application/json',
      },
      body: JSON.stringify(keenEvent),
      },
      function (e, r, body) {
        if( e ) return callback(e,user,context);
        //We assume everything went well
        user.persistent.signedUp = true;
        return callback(null, user, context);
       });

}

function(user, context, callback) {

  var splunkBaseUrl = 'YOUR SPLUNK SERVER, like: https://your server:8089';

  //Add any interesting info to the event
  var event = {
    message: user.signedUp ? 'Login' : 'SignUp',
    application: context.clientName,
    clientIP: context.request.ip,
    protocol: context.protocol,
    userName: user.name,
    userId: user.user_id
  };

  request.post( {
    url: splunkBaseUrl + '/services/receivers/simple',
    auth: {
        'user': 'YOUR SPLUNK USER',
        'pass': 'YOUR SPLUNK PASSWORD',
      },
    json: event,
    qs: {
      'source': 'auth0',
      'sourcetype': 'auth0_activity'
    }
  }, function(e,r,b) {
    if (e) return callback(e);
    if (r.statusCode !== 200) return callback(new Error('Invalid operation'));
    user.persistent.signedUp = true;
    return callback(e, user, context);
  });

}

function (user, context, callback) {

  var baseURL = configuration.FIREBASE_URL;
  var secret = configuration.FIREBASE_SECRET;
  var fb_id = new Buffer(user.user_id).toString('base64');

  var fbIdentity = {
    "identity": {
      "user_id": user.user_id,
      "email": user.email,
      "name": user.name,
      "nickname": user.nickname,
      "picture": user.picture
    }
  };

  var putURL = baseURL + "/users/" + fb_id + ".json?auth=" + secret;
  request.put({
    "url": putURL,
    "json": fbIdentity
  },
  function(err, response, body) {
    if (err) return callback(err);
    return callback(null, user, context);
  });
}

function (user, context, callback) {
  var ZAP_HOOK_URL = 'REPLACE_ME';

  var small_context = {
    appName: context.clientName,
    userAgent: context.userAgent,
    ip: context.ip,
    connection: context.connection,
    strategy: context.connectionStrategy
  };
  var payload_to_zap = extend({}, user, small_context);
  request.post({
    url: ZAP_HOOK_URL,
    json: payload_to_zap
  },
  function (err, response, body) {
    // swallow error
    callback(null, user, context);
  });

  function extend(target) {
    for (var i = 1; i < arguments.length; i++) {
      var source = arguments[i],
          keys = Object.keys(source);

      for (var j = 0; j < keys.length; j++) {
          var name = keys[j];
          target[name] = source[name];
      }
    }
    return target;
  }
}

function (user, context, callback) {
  var ZAP_HOOK_URL = 'REPLACE_ME';

  // short-circuit if the user signed up already
  if (user.signed_up) return callback(null, user, context);

  var small_context = {
    appName: context.clientName,
    userAgent: context.userAgent,
    ip: context.ip,
    connection: context.connection,
    strategy: context.connectionStrategy
  };
  var payload_to_zap = extend({}, user, small_context);
  request.post({
    url: ZAP_HOOK_URL,
    json: payload_to_zap
  },
  function (err, response, body) {
    // swallow error
    // mark the user as `signed_up` so next time the rule does not execute
    user.persistent.signed_up = true;
    callback(null, user, context);
  });

  function extend(target) {
    for (var i = 1; i < arguments.length; i++) {
      var source = arguments[i],
          keys = Object.keys(source);

      for (var j = 0; j < keys.length; j++) {
          var name = keys[j];
          target[name] = source[name];
      }
    }
    return target;
  }
}

function (user, context, callback) {

  var deskSubDomain = 'YOUR DESK SUBDOMAIN'; //https://{subdomain}.desk.com
  var deskAPIKey = 'YOUR DESK MULTIPASS API KEY';

  //Mutipass with a 5 min expiration
  var deskToken = {
    "uid": user.user_id,
    "expires": (new Date(new Date().getTime() + (5 * 60 * 1000))).toISOString(),
    "customer_email": user.email,
    "customer_name": user.name
  };

  var mp = multipass(deskToken, deskAPIKey, deskSubDomain);
  var signature = sign(deskAPIKey, mp);

  user.desk_login_url = 'https://' + deskSubDomain + '.desk.com/customer/authentication/multipass/callback?multipass=' + encodeURIComponent(mp) + '&signature=' + encodeURIComponent(signature);

  function sign(key, multipass)
  {
    var sha1 = crypto.createHmac('sha1',key);
    return sha1.update(multipass).digest('base64');
  }

  function multipass(jsonData, apiKey, site) {
    var data = new Buffer(JSON.stringify(jsonData), 'utf8');
    var key = crypto.createHash('sha1').update(apiKey + site).digest('binary').substring(0, 16);
    var iv = new Buffer('OpenSSL for Ruby', 'utf8');

    var pad = 16 - (data.length % 16),
        paddedData = new Buffer(data.length + pad);
    data.copy(paddedData);
    for (var i = data.length, len = paddedData.length; i < len; ++i) {
      paddedData[i] = pad;
    }
    // Encrypt with AES
    var cipher = crypto.createCipheriv('aes-128-cbc', key, iv);
    cipher.setAutoPadding(false);
    var token = cipher.update(paddedData);

    var mp = Buffer.concat([iv, token]);

    return mp.toString('base64');
  }

  callback(null, user, context);
}
